mapper.insert:
INSERT INTO account(
balance, belong, createTime, name, pwd, isreal, destinationId, destinationtype,
) VALUES (
#{balance}, #{belong}, #{createTime}, #{name}, password(#{pwd}), #{isReal}, #{destinationId}, #{destinationType},
)

mapper.update:
UPDATE account SET
balance=#{balance}, belong=#{belong}, createTime=#{createTime}, name=#{name}, isreal=#{isReal}, destinationId=#{destinationId}, destinationType=#{destinationType},
WHERE id=#{id}
